import React, { useState } from 'react';
import axios from 'axios';
import './css/input.css';
import Banner from './Banner';

function Login() {
    const [email, setEmail] = useState('');         //EMAIL
    const [password, setPassword] = useState('');   //PASSWORD
    const [message, setMessage] = useState('');     // Success or error message
    const [error, setError] = useState(null);       // Error handling

    //ACCOUNT LOGIN
    const handleSubmit = async (e) => {
        e.preventDefault();

        const url = 'http://localhost:5000/api/auth/login';

        // BUTTON PRESSED
        try {
            const response = await axios.post(url, {email, password}, { withCredentials: true });  //BACKEND REQUEST
                                                                      //withCredentials accepts cookies
            if (response.status===200) {
              window.location.href = '/account';
            }
        } catch (error) {
            setError(error.response.data.message);
            setMessage('');   //clear success
        }
    };

    return (
      <div>
        <Banner/>
        <div class="flex flex-col items-center bg-[#101c26] min-h-screen" >
          <div className='max-w-6xl mx-auto p-4 md:p-16 xl:p-20'>
            <div className='lg:w-2/3 space-y-5 text-center mx-auto'>
              <h2 class ='text-white uppercase tracking-wides font-medium text-4xl cursor-default'>Login</h2>
              <div className='h-0.5 bg-red-500 w-14 mx-auto'></div>
            </div>
          </div>
          <form onSubmit={handleSubmit}>
            <div class="flex items-center space-x-9">
              <label class = 'text-white'>Email:</label>
              <input
                type="email"
                value={email}
                onChange={(e) => {              //set var and clear messages
                  setEmail(e.target.value);
                  setMessage('');
                  setError(null);
                }}
                required
                class="className='border text-gray-900 text-sm rounded focus:ring-0 focus:border-gray-400 block w-full p-3 bg-zinc-700/20 border-zinc-700/50 placeholder:text-gray-300/50 text-white'"
              />
            </div>
            <div class="space-x-2">
              <label>Password:</label>
              <input
                type="password"
                value={password}
                onChange={(e) => {              //set var and clear messages
                  setPassword(e.target.value);
                  setMessage('');
                  setError(null);
                }}
                required
                class="flex-grow border rounded px-5 mb-1"
              />
          </div>
          
          <button type="submit" class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-3 rounded mb-5">
            Login
          </button>
        </form>

        <span class="cursor-default">New to UniVentures? <a href="/signup" class="text-red-400">Create an account</a></span>

          {/* Display success message */}
          {message && <p className="success font-bold">{message}</p>}

          {/* Display error message */}
          {error && <p className="error font-bold">{error}</p>}
      </div>
    </div>
    );
}

export default Login;
